const intitialState = {
  totalCost: 0,
  totalProducts: 0,
  items: [],
};

export default (state = intitialState, action) => {
  switch (action.type) {
    case "ADD_TO_CART": {
      return state.items.find((item) => item.code === action.product.code)
        ? {
            totalProducts: state.totalProducts + 1,
            totalCost: state.totalCost + action.product.cost,
            items: state.items.map((item) => {
              if (item.code === action.product.code) {
                return {
                  ...item,
                  qantity: item.qantity + 1,
                };
              }
            }),
          }
        : {
            totalProducts: state.totalProducts + 1,
            totalCost: state.totalCost + action.product.cost,
            items: [...state.items],
          };
    }
    case "SET_QUANTİTY": {
      let reviseQuantity = state.items.map((item) => {
        if (item.code === action.code) {
          return {
            ...item,
            quantity: action.quantity,
          };
        }
        return item;
      });

      let reviseTotalQuantity = reviseQuantity.reduce(
        (prev, curr) => prev + curr.quantity,
        0
      );

      let reviseTotalCost = reviseQuantity.reduce(
        (prev, curr) => prev + curr.qantity * curr.cost,
        0
      );

      return {
        totalProducts: reviseTotalQuantity,
        totalCost: reviseTotalCost,
        items: reviseQuantity,
      };
    }

    case "REMOVE_ITEM": {
      let findItem = state.item.find(item => item.code === action.code);
      if(findItem){
        let totalCostOfItem = findItem.qantity * findItem.cost;
        return{
          totalProducts: state.totalProducts - findItem.qantity,
          totalCost: state.totalCost - totalCostOfItem,
          item: state.item.filter(item => item.code !== action.code)
        }
      } else {
        return state;
      }
    }
    default: {
      return state;
    }
  }
};
